INSERT INTO `noticias` (`id`, `data_cadastro`, `usuario`, `data_noticia`, `data_cadastro_formatada`, `tbl`, `categoria`, `titulo`, `resumo`, `descricao`, `imagem_nome`, `imagem_url`, `indice`, `id_entidade_orgaos`, `id_mandatos`, `id_admin`, `ativo`) VALUES
(198, '2024-02-27 10:59:01', 'root', '2017-04-07', '', 'noticias', '#Administração', 'O vereador Gilberto Barbosa de Oliveira fez um relato a cerca da importância da reunião ocorrida no SENAC.', 'NULL', 'O vereador Gilberto Barbosa de Oliveira fez um relato a cerca da importância da reunião ocorrida no SENAC para tratar da situação da agencia do banco do Brasil que foi alvo de ataque de bandidos recentemente, lembrou a alto valor relativo a FIES e agricultura na agencia de Cedro destacando a atuação do prefeito Nilson Diniz para que tenhamos de volta a importante instituição bancaria o mais breve possível,  disse que faz parte de um projeto político encabeçado pelo atual gestor que respeita a população lembrando que as estradas são recuperadas sem que a maquina levante a lâmina em nenhuma localidade por questões politicas, informou a cerca de visitas que realizou em varias localidades do município recentemente.', 'NULL', 'https://www.camaradecedro.ce.gov.brNULL', 0, NULL, 0, NULL, NULL),
(199, '2024-02-27 10:59:01', 'root', '2017-04-07', '', 'noticias', '#Administração', 'O vereador Gilberto Barbosa de Oliveira fez um relato a cerca da importância da reunião ocorrida no SENAC.', 'NULL', 'O vereador Gilberto Barbosa de Oliveira fez um relato a cerca da importância da reunião ocorrida no SENAC para tratar da situação da agencia do banco do Brasil que foi alvo de ataque de bandidos recentemente, lembrou a alto valor relativo a FIES e agricultura na agencia de Cedro destacando a atuação do prefeito Nilson Diniz para que tenhamos de volta a importante instituição bancaria o mais breve possível,  disse que faz parte de um projeto político encabeçado pelo atual gestor que respeita a população lembrando que as estradas são recuperadas sem que a maquina levante a lâmina em nenhuma localidade por questões politicas, informou a cerca de visitas que realizou em varias localidades do município recentemente.', '/fotos/199/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/199/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(200, '2024-02-27 10:59:01', 'root', '2017-04-07', '', 'noticias', '#Administração', 'O vereador José Amarilo Sampaio Junior iniciou parabenizando o presidente deste poder pelos avanços e inovações.', 'NULL', 'O vereador José Amarilo Sampaio Junior iniciou parabenizando o presidente deste poder pelos avanços e inovações destacando a transmissão das sessões via emissoras de rádio como Cedro FM e Montevidéo AM e em seguida fez um relato sobre a precariedade com que o SAMU está funcionando em Cedro faltando vigilante e ainda com as instalações precárias faltando gás de cozinha e itens para a alimentação o que constatou através de visita ao local, no que apela para uma providencia urgente para evitar que o município venha a perder esse importante serviço, voltou a cobrar a cerca da asa de apoio em Fortaleza dizendo que o caso ainda não foi solucionado e as reclamações continuam através da população.', 'NULL', 'https://www.camaradecedro.ce.gov.brNULL', 0, NULL, 0, NULL, NULL),
(201, '2024-02-27 10:59:01', 'root', '2017-04-07', '', 'noticias', '#Administração', 'O vereador José Amarilo Sampaio Junior iniciou parabenizando o presidente deste poder pelos avanços e inovações.', 'NULL', 'O vereador José Amarilo Sampaio Junior iniciou parabenizando o presidente deste poder pelos avanços e inovações destacando a transmissão das sessões via emissoras de rádio como Cedro FM e Montevidéo AM e em seguida fez um relato sobre a precariedade com que o SAMU está funcionando em Cedro faltando vigilante e ainda com as instalações precárias faltando gás de cozinha e itens para a alimentação o que constatou através de visita ao local, no que apela para uma providencia urgente para evitar que o município venha a perder esse importante serviço, voltou a cobrar a cerca da asa de apoio em Fortaleza dizendo que o caso ainda não foi solucionado e as reclamações continuam através da população.', '/fotos/201/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/201/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(202, '2024-02-27 10:59:01', 'root', '2017-04-07', '', 'noticias', '#Administração', 'O Vereador Francre Alves de Oliveira iniciou agradecendo ao secretario do meio ambiente Elesson Barbosa pelo atendimento.', 'NULL', 'O Vereador Francre Alves de Oliveira iniciou agradecendo ao secretario do meio ambiente Elesson Barbosa pelo atendimento feito em beneficio da população do bairro açude novo  através de um pedido seu, enalteceu o trabalho do presidente da casa com inovações neste poder, informou ter sido procurado por estudantes do sitio baixio do Bento reclamando que estão se deslocando até a nação para apanhar o transporte o que apela para uma providencia da pasta competente, solicitou reforma nos canteiros central da alameda José Quintino e uma passagem de nível ligando a rua Pedro Lopes Vieira a rua Vicente Viana.', '/fotos/202/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/202/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(203, '2024-02-27 10:59:01', 'root', '2017-04-07', '', 'noticias', '#Administração', 'O vereador Apolinario Gomes de Souza também destacou as inovações nesse poder parabenizando o presidente.', 'NULL', 'O vereador Apolinario Gomes de Souza também destacou as inovações nesse poder parabenizando o presidente pelos feitos, agradeceu ao secretario da infra estrutura pelo atendimento ao pleito de sua autoria para conserto em um trecho da estrada de assunção e apela para que seja escoado um esgoto a céu aberto existente nas proximidades da borracharia do Nêgo ate a praça Antonio Marques.', '/fotos/203/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/203/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(204, '2024-02-27 10:59:01', 'root', '2017-04-07', '', 'noticias', '#Administração', 'O Vereador Antonio Helio Diniz Bezerra relatou sobre o episódio envolvendo a agencia do banco do Brasil local.', 'NULL', 'O Vereador Antonio Helio Diniz Bezerra relatou sobre o episódio envolvendo a agencia do banco do Brasil local ressaltando que esse poder fará na sua integralidade o que for possível para reabrir o banco, destacou a necessidade de um trabalho de todos para a melhoria da segurança pública nesse município e apelou para a reabertura imediatamente dos banheiros externos do mercado público no centro da cidade a pedido dos comerciantes dos quiosques, parabenizou a secretaria de educação pela abertura do diálogo com os motoristas da pasta apelando para que seja feito o mesmo com os secretários escolares.', '/fotos/204/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/204/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(205, '2024-02-27 10:59:01', 'root', '2017-04-07', '', 'noticias', '#Administração', 'A vereadora Silvana Maria Coelho Leite Pinheiro lamentou de inicio a falta d água em vários bairros da cidade.', 'NULL', 'A vereadora Silvana Maria Coelho Leite Pinheiro lamentou de inicio a falta d água em vários bairros da cidade solicitando por conta disso a presença neste poder da representação da CAGECE para tratar do assunto, solicitou do poder público a implantação de pavimentação asfáltica iniciando na rua Celso Alves de Araújo até o bairro alto da floresta e ainda o conserto na rua Rosita Afonso no jardim Afonso Celso, informou a cerca das melhorias e avanços na merenda escolar informando que esteve com a nutricionista Paula Rejane que detalhou sobre o cardápio variado com 30 itens onde 15 são da agricultura familiar, finalizou asseverando que os avanços são visíveis.', '/fotos/205/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/205/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(206, '2024-02-27 10:59:01', 'root', '2017-04-07', '', 'noticias', '#Administração', 'A vereadora Ana Nilma de Freitas Diniz ressaltou que sempre na sua atuação parlamentar defendeu.', 'NULL', 'A vereadora Ana Nilma de Freitas Diniz ressaltou que sempre na sua atuação parlamentar defendeu o contestar ressaltando que a democracia permite a cada um expor seu ponto de vista e atualmente defende um projeto concreto que melhorou significativamente a qualidade de vida do povo, lamentando o que testemunhou em administrações passadas um verdadeiro desastre nessa terra, informou sobre sua ultima viagem a fortaleza onde através da secretaria das cidades está viabilizando liberação de fogões ecológicos para esse município através das associações comunitárias, elogiou a inciativa do prefeito Nilson Diniz diante do ataque que destruiu a agencia do banco do Brasil e o gestor tomou providencias imediatas com o objetivo de retornar os serviços para nossa população, finalizou destacando a atuação da secretaria de educação  local com melhorias incontestáveis em todos os seguimentos da pasta.', '/fotos/206/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/206/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(207, '2024-02-27 10:59:01', 'root', '2017-04-07', '', 'noticias', '#Administração', 'O Vereador José de Araujo Manzolillo Lobo informou sobre uma moção de repúdio encabeçada pela União dos Vereadores do Ceará', 'NULL', 'O Vereador José de Araujo Manzolillo Lobo informou sobre uma moção de repúdio encabeçada pela União dos Vereadores do Ceará UVC contra a reforma da previdência social, apresentou aos colegas disposição do Tribunal regional Federal pela absorvição dos senhores Francisco Alberto Fernandes de Souza, João Viana de Araujo e Rodrigo Xavier de Araujo no caso do garantia safra, lamentou o fato do município não está cumprindo com sua parte na despesa do SAMU alertando para o prazo oferecido pelo estado para os reparos inclusive já esgotado, solicitou a conclusão da colocação do alambrado na quadra do distrito de Candeias,', '/fotos/207/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/207/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(208, '2024-02-27 10:59:01', 'root', '2017-04-11', '', 'noticias', '#Administração', 'O dia 11 de Abril é lembrado como o Dia do Prefeito Municipal, que é o político eleito por determinado município para exercer o cargo de gestor público da cidade pelo período de 4 anos, podendo ser reeleito por mais 4 anos.', 'NULL', 'O cargo de Prefeito faz parte do poder executivo, sendo responsável por administrar o município conforme as leis municipais. Dentro da ordem constitucional, o prefeito tem o cargo de chefia da administração dos serviços públicos, equiparando-se aos demais chefes do poder executivo como Governadores e Presidentes, no âmbito local do município, tendo como fiscalizadores de suas atividades o poder legislativo representado pelos vereadores da câmara municipal.', '/fotos/208/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/208/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(215, '2024-02-27 10:59:01', 'root', '2017-04-12', '', 'noticias', '#Administração', 'Imagens das ultimas Sessões Ordinária/Extraordinária, realizada na sede do Poder Legislativo.', 'NULL', '', '/fotos/215/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/215/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(216, '2024-02-27 10:59:01', 'root', '2017-04-12', '', 'noticias', '#Administração', 'O Vereador Francre Alves de Oliveira expressou o prazer de fazer parte desse poder.', 'NULL', 'O Vereador Francre Alves de Oliveira expressou o prazer de fazer parte desse poder de forma independente para trabalhar com toda dedicação em prol do povo sem ser manipulado por ninguém, asseverou que está cumprindo com sua obrigação de fiscalização da coisa publica em seguida solidarizou-se com os moradores das comunidades de recanto e malhada vermelha pela situação precária das estradas relatando sobre um trecho de atoleiro próximo a escola do recanto onde está intransitável momento em que apelou ao secretário da infra estrutura para ver com bons olhos essa questão do recanto.', '/fotos/216/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/216/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(217, '2024-02-27 10:59:01', 'root', '2017-04-12', '', 'noticias', '#Administração', 'O Vereador Matheus Guedes Araujo iniciou prestando contas de sua viagem a Fortaleza.', 'NULL', 'O Vereador Matheus Guedes Araujo iniciou prestando contas de sua viagem a Fortaleza informando que pleiteou junto ao deputado estadual Leonardo Pinheiro uma ambulância para esse município mais precisamente para o vale do São miguel, em seguida lamentou uma postagem feita pelo secretario de infra estrutura nas redes sociais criticando o trabalho de fiscalização realizado pelo grupo G6 o que diz não admitir esse tipo de posicionamento ressaltando que está ao lado do povo, informou sobre o caso de uma jovem do sítio cariuzinho com uma fratura no braço lamentando que só depois de 3 dias houve o atendimento no município de Icó e a mesma está ha 23 dias aguardando a cirurgia o que apela para uma solução do caso.', '/fotos/217/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/217/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(218, '2024-02-27 10:59:01', 'root', '2017-04-12', '', 'noticias', '#Administração', 'O Vereador José de Araujo Manzolillo Lobo relatou sobre as votações nesse poder.', 'NULL', 'O Vereador José de Araujo Manzolillo Lobo relatou sobre as votações nesse poder e ressaltou que sempre ira votar matérias com responsabilidade dizendo não ter dúvida que a revogação do pedido de urgência urgentíssima em torno da matéria do executivo que tramita na casa foi oportuna, relatou sobre uma matéria publicada no jornal o povo que mostra a situação dos transportes escolares em Cedro onde mostra inúmeros veículos em situação irregular conforme dados do DETRAN e ainda reportou-se a um acidente com um transporte escolar acontecido recentemente numa ladeira de acesso a cidade.', '/fotos/218/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/218/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(219, '2024-02-27 10:59:01', 'root', '2017-04-12', '', 'noticias', '#Administração', 'O Vereador Francisco Juceza Teixeira Felipe reportou-se a situação da agencia do banco do Brasil.', 'NULL', 'O Vereador Francisco Juceza Teixeira Felipe reportou-se a situação da agencia do banco do Brasil informando que esteve em Iguatu e as notícias com relação ao retorno dos trabalhos em Cedro não são animadoras e que as despesas serão em torno de um milhão de reais, disse que o episódio está causando prejuízos enormes a nossa população, finalizou parabenizando o colega Amarilo Junior pelo título de cidadão cedrense muito merecido.', '/fotos/219/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/219/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(220, '2024-02-27 10:59:01', 'root', '2017-04-12', '', 'noticias', '#Administração', 'O Vereador Marcelo Roque de Matos reafirmou em seu pronunciamento que no ano de 2012.', 'NULL', 'O Vereador Marcelo Roque de Matos reafirmou em seu pronunciamento que no ano de 2012 o transporte escolar era precário e os alunos eram transportados em carros pau de arara inclusive com registros de acidentes na época, ressaltou que o acidente recentemente citado pelo colega José de Araujo não aconteceu no horário do serviço de transporte de alunos e mesmo assim presenciou as providencias tomadas pela senhora secretaria de educação, disse que os avanços na area são notórios e a fiscalização do DETRAN levou em consideração detalhes como tacógrafo e lanternas quebradas que não causam maiores prejuízos alunos.', '/fotos/220/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/220/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(221, '2024-02-27 10:59:01', 'root', '2017-04-12', '', 'noticias', '#Administração', 'A Vereadora na Nilma de Freitas Diniz iniciou informando com satisfação a distribuição de 70 mil alevinos.', 'NULL', 'A Vereadora na Nilma de Freitas Diniz iniciou informando com satisfação a distribuição de 70 mil alevinos beneficiando 35 açudes desse município, lamentou que quando se fala em transporte escolar tem alguém torcendo para o quanto pior melhor lembrando que no passado o transporte pau de arara era uma realidade que causou acidentes deixando inclusive sequelas citando o caso de um pessoa com um trauma no nariz que nem sequer conseguiu a cirurgia para corrigir o problema, disse que nas administrações passadas o suco era de garrafa com bolacha creme craker com casos de escolas 3 meses sem merenda e atualmente existe um cardápio variado com polpa de frutas, ovo de codorna e muito mais, citou o que tachou de irresponsabilidade a falta do repasse na verba do INSS em administrações passadas com caso de funcionário com empréstimo consignado tinha o valor do débito descontado e não acontecia o repasse para abater na conta, finalizou enaltecendo o vereador Amarilo Junior dizendo ser uma honra votar um título de cidadão cedrense para o mesmo.', '/fotos/221/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/221/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(222, '2024-02-27 10:59:01', 'root', '2017-04-12', '', 'noticias', '#Administração', 'O vereador Antonio Helio Diniz Bezerra iniciou parabenizando o secretario de agricultura.', 'NULL', 'O vereador Antonio Helio Diniz Bezerra iniciou parabenizando o secretario de agricultura pelo trabalho de peixamento dos açudes, parabenizou o colega Amarilo Junior pelo título de cidadão cedrense e em seguida disse ter conhecimento que os lotes da licitação da merenda escolar que estavam prejudicados foram sanados e o ganhador foi o empresario Erasmo Ferreira, asseverou que ha mais de um mês os vereadores vem cobrando o conserto das estradas e ao invés do secretario de infra estrutura está fazendo postagem no face book a cerca do trabalho de fiscalização do G6 deveria conseguir com o prefeito mais caçambas para realizar o piçarramento das estradas, finalizou dizendo que o trabalho em defesa do povo terá continuidade.', '/fotos/222/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/222/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(223, '2024-02-27 10:59:01', 'root', '2017-04-12', '', 'noticias', '#Administração', 'O vereador Gilberto Barbosa de Oliveira relatou a cerca das melhorias na área de educação.', 'NULL', 'O vereador Gilberto Barbosa de Oliveira relatou a cerca das melhorias na área de educação destacando o transporte escolar ressaltando que se existir irregularidade que seja corrigido, reiterou que defende um projeto político deixando claro que cumpre sua missão de fiscalizar todos os dias e seu partido é o Cedro, elogiou o trabalho do secretário de agricultura no peixamento dos açudes  lembrando que apresentou requerimento nesse sentido, lembrou que a dívida do INSS desse município era de trinta milhões e a atual gestão conseguiu colocar em dia, encerrou relatando a cerca de varias reuniões que vem realizando nas comunidades do meio rural.', '/fotos/223/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/223/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(224, '2024-02-27 10:59:01', 'root', '2017-04-12', '', 'noticias', '#Administração', 'O Vereador José Amarilo Sampaio Junior iniciou agradecendo aos colegas pela concessão do título.', 'NULL', 'O Vereador José Amarilo Sampaio Junior iniciou agradecendo aos colegas pela concessão do título de cidadão cedrense, em seguida disse reconhecer as melhorias no transporte escolar mas alguns pontos necessitam de melhorias citando comunidades como São Vicente e mundo novo onde os alunos perderam aulas por problemas no transporte, citou vários problemas como desgaste peneus e ainda relatou a cerca do acidente envolvendo um veículo da frota de transporte escolar que colidiu com um imóvel do senhor José Carneiro afirmando que o mesmo estava sim no horário de serviço embora os alunos haviam descido um trecho antes do acontecido, lamentou o fato do distrito de Santo Antonio está ha dois meses sem o atendimento médico o que apela para providencias e finalizou afirmando que o secretario de infra estrutura foi infeliz na sua postagem a nível de face book relacionada ao trabalho de fiscalização do G¨6.', '/fotos/224/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/224/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(225, '2024-02-27 10:59:01', 'root', '2017-04-20', '', 'noticias', '#Administração', 'Imagens das ultimas Sessões Ordinária/Extraordinária, realizada na sede do Poder Legislativo.', 'NULL', '', '/fotos/225/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/225/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(226, '2024-02-27 10:59:01', 'root', '2017-04-20', '', 'noticias', '#Administração', 'O Vereador José de Araujo Manzolillo Lobo informou sobre a greve da classe de motoristas.', 'NULL', 'O Vereador José de Araujo Manzolillo Lobo informou sobre a greve da classe de motoristas da área de educação lamentando o fato de ver mais uma classe vestindo preto nesse município, reportou-se ao que foi dito em sessão passada a cerca da sua atuação afirmando que obteve 673 votos e seu grupo obteve 8.140 votos destacando que representa uma família, ressaltou que foi candidato a convite do seu tio João Viana de Araujo e foi eleito mesmo tendo chegado já bem próximo a data do pleito tudo isso graças a sua família a quem tem grande respeito, citou inúmeros terrenos doados por familiares para construções em beneficio do povo em geral, asseverando que ninguém tem o direito de destratar colega dizendo que nesta casa não representa ninguém, lamentou a citação destacando que está sim para representar o povo.', '/fotos/226/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/226/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(227, '2024-02-27 10:59:01', 'root', '2017-04-20', '', 'noticias', '#Administração', 'O vereador Francre Alves de Oliveira apresentou votos de pesar pelo falecimento do senhor.', 'NULL', 'O vereador Francre Alves de Oliveira apresentou votos de pesar pelo falecimento do senhor Antonio Balbino e em seguida defendeu indicação de sua autoria para implantação de banheiros químicos no terminal rodoviário local, lembrou a necessidade da formação das comissões permanentes desse poder e enalteceu os esclarecimentos apresentados pelo senhor João Batista Moura nessa sessão.', '/fotos/227/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/227/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(228, '2024-02-27 10:59:01', 'root', '2017-04-20', '', 'noticias', '#Administração', 'O Vereador Matheus Guedes Araujo prestou contas na tribuna de sua ultima viagem a Fortaleza.', 'NULL', 'O Vereador Matheus Guedes Araujo prestou contas na tribuna de sua ultima viagem a Fortaleza informando que esteve no gabinete do Deputado Leonardo Pinheiro pleiteando poços profundos junto a SOHIDRA e através do Deputado Danilo Forte pleiteou junto ao DER a pavimentação asfáltica da agrovila, disse que falou pessoalmente com o superintendente do DER Sergio Azevedo sobre a retirada da curva da picada, apelou para o conserto imediato do trecho da estrada na chegada do distrito de Assunção narrando que um ônibus chegou a atolar no local.', '/fotos/228/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/228/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(229, '2024-02-27 10:59:01', 'root', '2017-04-20', '', 'noticias', '#Administração', 'O Vereador Antonio Helio Diniz Bezerra informou aos professores de 200 horas.', 'NULL', 'O Vereador Antonio Helio Diniz Bezerra informou aos professores de 200 horas que o projeto de aumento chegou a essa casa legislativa no dia 24 de março e foi retirado pelo autor no dia 28 de março justificando e assegurando que não existe atraso na votação do mesmo por culpa do G 6 e tratando-se das irregularidades no transporte escolar assevera que o grupo G 6 vem fazendo o trabalho de fiscalização com seriedade  e disse lamentar a greve dos motoristas por conta dos prejuízos aos alunos mas a classe tentou de todas as formas e não houve o atendimento, disse que os secretários escolares também não conseguiram lamentando que nem sequer uma ajuda de custo foi oferecida aos mesmos.', '/fotos/229/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/229/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(230, '2024-02-27 10:59:01', 'root', '2017-04-20', '', 'noticias', '#Administração', 'O vereador Gilberto Barbosa de Oliveira informou que foi confiada a sua pessoa a liderança do governo.', 'NULL', 'O vereador Gilberto Barbosa de Oliveira informou que foi confiada a sua pessoa a liderança do governo e que continua defendendo um projeto político, sobre o diálogo com a categoria de secretários escolares informou que o setor contábil fez a explanação devida e ficou acertado o percentual que foi apresentado justificando que por coerência não votou no projeto de indicação, reafirmou que está nessa casa para defender os interesses do povo, disse ter participado da paixão de Cristo em Cedro na sexta feira santa enaltecendo o evento assim como um filme que foi exibido no distrito de Santo Antonio através do instituto Zuza Laurenio.', '/fotos/230/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/230/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(231, '2024-02-27 10:59:01', 'root', '2017-04-20', '', 'noticias', '#Administração', 'O vereador Francisco Juceza Teixeira Felipe fez um relato a cerca da importância do trabalho da pastoral.', 'NULL', 'O vereador Francisco Juceza Teixeira Felipe fez um relato a cerca da importância do trabalho da pastoral carcerário nesse município onde uma médica cubana consultou diversos presos na cadeia pública local, destacando o trabalho do Padre Ademar e apresentando seu apoio a moção de congratulação ao mesmo.', '/fotos/231/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/231/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(232, '2024-02-27 10:59:01', 'root', '2017-04-20', '', 'noticias', '#Administração', 'O vereador Marcelo Roque de Matos inciou agradecendo a administração.', 'NULL', 'O vereador Marcelo Roque de Matos inciou agradecendo a administração pelo atendimento a seu pleito para concerto do trecho da estrada que liga as comunidades de teú / mundo novo, detalhou sobre o trabalho hídrico importante desenvolvido em comunidades do distrito de Várzea da Conceição informando que existem novos projetos em andamento, disse lamentar a paralisação dos motoristas e afirma que houve diálogo sim e a procuradoria se surpreendeu com a decisão visto que havia um acerto mas a categoria assim decidiu é um direito deles, encerrou reafirmando seu compromisso com os trabalhadores inclusive destacando o fato de fazer parte de um partido que ao longo da história defendeu todas as classes.', '/fotos/232/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/232/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(233, '2024-02-27 10:59:01', 'root', '2017-04-20', '', 'noticias', '#Administração', 'A Vereadora Ana Nilma de Freitas Diniz reportou-se ao pronunciamento do colega.', 'NULL', 'A Vereadora Ana Nilma de Freitas Diniz reportou-se ao pronunciamento do colega José de Araujo Mamzolillo Lobo ressaltando que nesta casa não está para representar família \"A\" ou \"B\", e sim a população do município, lembrou que ingressou na política com o aval do líder médico Dr. Obi Viana Diniz homem de grandes qualidades, relatando que votação expressiva que obteve no pleito anterior a esse for por conta de uma comoção pela situação de saúde que seu esposo passava naquele momento tendo sido uma quantidade de voto que acha que jamais alguém alcançará, disse que no pleito passado reelegeu-se graças ao seu trabalho e está muito feliz com o carinho e apoio da população, com relação aos professores disse está tranquila pelos avanços  e investimentos onde o recurso é realmente bem empregado, reportou-se a projetos importantes de abastecimento de água e finalizou destacando a homenagem recebida pela poetisa Idalzira Bezerra (im memoriam) na bienal do livro em Fortaleza.', '/fotos/233/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/233/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(234, '2024-02-27 10:59:01', 'root', '2017-04-20', '', 'noticias', '#Administração', 'A Vereadora Silvana Maria Coelho Leite Pinheiro iniciou relatando sobre a encenação da paixão de Cristo.', 'NULL', 'A Vereadora Silvana Maria Coelho Leite Pinheiro iniciou relatando sobre a encenação da paixão de Cristo momento em que parabenizou o Padre Ademar Alves e toda equipe que trabalhou voluntariamente no espetáculo com destaque para o artista Ertim Morais, lamentou a falta de água nos bairros destacando a necessidade de uma explicação por parte da CAGECE, enalteceu o programa nacional rural parabenizando a secretária de ação social e o senhor João Batista Moura que explanou hoje nesta casa sobre o plano diretor, finalizou parabenizando o gestor Nilson Diniz pelas ações em prol da população.', '/fotos/234/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/234/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(235, '2024-02-27 10:59:01', 'root', '2017-04-20', '', 'noticias', '#Administração', 'O Vereador Apolinário Gomes de Souza iniciou agradecendo ao secretário da infraestrutura.', 'NULL', 'O Vereador Apolinário Gomes de Souza iniciou agradecendo ao secretário da infraestrutura pelo atendimento a seu pleito no que se refere ao conserto da praça da divisão, requereu verbalmente melhorias na iluminação pública na rua Valder Viana, em seguida fez um relato a cerca da visita que fizeram na garagem municipal destacando que os vereadores foram muito bem recebidos pelo senhor Viana encarregado daquele setor dizendo que o mesmo tem boas intenções mas lamentavelmente a frota de máquinas está toda quebrada sem funcionamento estando apenas a retro escavadeira e uma caçamba em atividade, apelando ao prefeito municipal ajudar aquele cidadão a desempenhar seu trabalho.', '/fotos/235/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/235/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(236, '2024-02-27 10:59:01', 'root', '2017-05-05', '', 'noticias', '#Administração', 'Fotos da sessão realizada dia 05 de Maio de 2017', 'NULL', '', '/fotos/236/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/236/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(237, '2024-02-27 10:59:01', 'root', '2017-05-05', '', 'noticias', '#Administração', 'O vereador Matheus Araujo parabenizou seu irmão Airton Filho pelo seu aniversario.', 'NULL', 'O vereador Matheus Araujo parabenizou seu irmão Airton Filho pelo seu aniversario, agradeceu ao secretario de infraestrutura pela recuperação de algumas estradas na zona rural atendendo alguns Requerimentos de sua autoria deixando o facebook de lado, falou da preocupaçaõ com a falta de água o que levou o G-6 a gerencia da CAGECE a nível de região em Juazeiro através do doutor Galba, que informou que tudo está sob controle. Reportou-se a manifestação do dia 28 de abril que iniciou com calma mas tomou conotação de ato político, disse ser contra as reformas trabalhistas e previdenciária.', '/fotos/237/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/237/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(238, '2024-02-27 10:59:01', 'root', '2017-05-05', '', 'noticias', '#Administração', 'O Vereador José Amarilo Sampaio Junior, solicitou moção de pesar pelo falecimento do senhor Ártur Felix de Araujo.', 'NULL', 'O Vereador José Amarilo Sampaio Junior, solicitou moção de pesar pelo falecimento do senhor Ártur Felix de Araújo de Varzea Alegre \"avô do radialista Claudio Araujo, da Rádio Montevideo de Cedro\", parabenizou seu pai Amarílo Sampaio pelo aniversario, disse que vem ouvindo da juventude que não existe ajuda nem incentivo para o esporte, disse que a própria seleçaõ de Cedro busca ajuda e a inscrição é paga pelos próprios atletas que pagam até o técnico para treina-los, apelou pela união de todos em prol do esporte, apresentou dados do portal da transparência da área do esporte, informou sobre uma conversa que teve com o prefeito de Varzea Alegre José Helder que informou sobre investimentos no esporte onde o custo de doze a vinte mil reais para realização de um campeonato.', '/fotos/238/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/238/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(239, '2024-02-27 10:59:01', 'root', '2017-05-05', '', 'noticias', '#Administração', 'O vereador Francre Alves falou sobre seu pedido para calçamento numa travessa ao lado da rádio.', 'NULL', 'O vereador Francre Alves falou sobre seu pedido para calçamento numa travessa ao lado da rádio Mandacaru FM, um trecho que ainda não tem nome, destacou a importância da reforma das estradas, disse ter sido procurado por moradores das imediações da praça dos ferroviarios onde o logradouro está abandonado com água de esgoto estagnada com mal cheiro e falta de iluminação pública, disse que o giradouro acumula água com risco de dengue dos moradores que pedem uma limpeza com retirada do lixo urgente.', '/fotos/239/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/239/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(240, '2024-02-27 10:59:01', 'root', '2017-05-05', '', 'noticias', '#Administração', 'O vereador Antonio Helio lamentou a falta de água constantemente nas residências, disse que esteve com os colegas na CAGECE.', 'NULL', 'O vereador Antonio Helio lamentou a falta de água constantemente nas residências, disse que esteve com os colegas na CAGECE regional em juazeiro para conversa com o gerente Dr.Galba, disse que foram informados da projeção para a ampliação do sistema de tratamento e construção de caixas de água. Disse  que o colega Amarílo Junior tem razão sobre o esporte, Cedro não tem crise, tem recurso em caixa e a realização de um campeonato é má vontade do gestor que não da condição ao secretário, falou da manifestação do dia 28 lembrando que foi o primeiro vereador que se posicionou contrário as reformas e ralatou que enviou a funcionaria Ana Patrícia até a casa paroquial e o Claudiano disse que não precisava abrir a Câmara. O Vereador disse que  sempre esteve ao lado do povo o que vem demostrando no voto ao contrário de outros ou outras que dizem está ao lado do povo e na votação ficam contrarios.', '/fotos/240/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/240/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(241, '2024-02-27 10:59:01', 'root', '2017-05-05', '', 'noticias', '#Administração', 'A vereadora Silvana Leite, informou que esteve em Fortaleza no DER.', 'NULL', 'A vereadora Silvana Leite, informou que esteve em Fortaleza no DER (departamento de estradas e rodagens), disse que esteve na casa Civil viabilizando perfuração de poços profundos, esteve na secretária de segurança com o major Lins secretário de segurança para tratar do pró-cidadania; esteve na secretaria de saúde tratando do consórcio da policlinica de Icó, disse que o secretário de esporte de Cedro conseguiu em Fortaleza materias para os desportistas e na parceria com a secretaria de infraestrutura foram construidos inumeros campos de futebol o que prova que o esporte não está parado, falou do grande projeto que breve será colocado em prática com aval do prefeito municipal que é um gestor inteligente que sabe da importância do esporte, sugeriu aos colegas vereadores buscarem atráves dos seus deputados para ajuda o esporte do Cedro.', '/fotos/241/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/241/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(242, '2024-02-27 10:59:01', 'root', '2017-05-05', '', 'noticias', '#Administração', 'A vereadora Ána Nilma disse que já foi até a CAGECE reclamar dos problemas causados por serviços não satisfatórios.', 'NULL', 'A vereadora Ána Nilma disse que já foi até a CAGECE reclamar dos problemas causados por serviços não satisfatórios, disse que o projeto de ampliação está pronto desde de 2014, através do prefeito Nilson Diniz que conseguiu a adutora em tempo recorde que evitou o município está hoje em colapso, uma obra de seis milhões de reais, disse que o prefeito Nilson, já entrou na funasa para implantação de reservatórios elevados no Alto do Padeiro, no pega Avoante e Jardim Afonso Celso para atendimento a contento durante 20 anos com valor de sete milhões de reais. Lembrou que o prefeito recebeu o Cedro cheio de corrupção de forma vergonhosa, disse que o Cedro não tem dengue nem chikungunya pela limpesa sistematíca feita por pessoas que recebem salário digno em dia com os agentes de endemias e de saúde coordenadas pela grande secretaria Sayonara Moura, disse que não vende ilusão nem usa de demagogia, disse que vota nessa casa de acorde com as suas convicções e pelo melhor para o povo.', '/fotos/242/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/242/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(243, '2024-02-27 10:59:01', 'root', '2017-05-05', '', 'noticias', '#Administração', 'O vereador Gilberto Barbosa informou que o distrito de Santo Antonio ficou quase noventa dias sem médico.', 'NULL', 'O vereador Gilberto Barbosa informou que o distrito de Santo Antonio ficou quase noventa dias sem médico e infelizmente alguem da comunidade cogitou que Dr. Plácido havia sido demitido o que não é verdade e o processo seletivo foi realizado e a Dra. Samara saldanha fonseca será empossada, disse que requeriu melhorias nas ruas da sede do Distrito de Candeias e já foi realizado o patrolhamento e piçarramento o que agradece em seu nome e do vice prefeito mas principalmente em nome dos moradores, lamentou as perdas com a ausencia das chuvas nos últimos dias.', '/fotos/243/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/243/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(244, '2024-02-27 10:59:01', 'root', '2017-05-05', '', 'noticias', '#Administração', 'O vereador Apolinário Gomes, apelou para limpeza no Bairro Jardim Afonso Celso.', 'NULL', 'O vereador Apolinário Gomes, apelou para limpeza no Bairro Jardim Afonso Celso, e o conserto de um esgoto a céu aberto nas proximidades do CSU, falou sobre a visita realizada a CAGECE em Juazeiro, falou sobre os problemas de iluminação pública e o secretario pitombeira esteve nesta casa dia 10 de março e anunciou a licitação que ate hoje não aconteceu embora a população paga mensalmente pelo serviço, sugeriu a ampliação do número de funcionários da limpeza conhecidos como verdinhos e solidarizou-se com os familiares da falecida dona Mariquinha de Seu Paloca.', '/fotos/244/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/244/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(245, '2024-02-27 10:59:01', 'root', '2017-05-05', '', 'noticias', '#Administração', 'O Vereador Marcelo Roque reportou-se as dificuldades causadas pela falta de água.', 'NULL', 'O Vereador Marcelo Roque reportou-se as dificuldades causadas pela falta de água onde a CAGECE muitas vezes demora a resolver alguns problemas na rede causando serios prejuizos principalmente as comunidades da zona rural, falou da sua viagem a Fortaleza onde no DER tratou de melhoria na entrada do distrito de Lagedo e Varzea da Conceição, informou que esteve na secretaria de abastecimento de água principalmente no Distrito de Várzea da Conceição, disse que esteve na Assembleia Legislativa no evento anti-droga, falou da manifestação do dia 28 e lembrou que deputados do Ceará votaram em contrario a vontade do povo e apelou para que todos façam contatos com seus  representantes, disse que dos três senadores cearenses dois são empregadores e acha difícil que eles votem a favor dos trabalhadores reafirmando repudiar as reformas, disse ter sido procurado por moradores da divisão reclamando de alguém que promove queimada que é um crime ambiental que prejudica a saúde apelando a secretaria de meio ambiente providencias no caso.', '/fotos/245/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/245/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(246, '2024-02-27 10:59:01', 'root', '2017-05-05', '', 'noticias', '#Administração', 'O vereador José de Araujo falou da visita na CAGECE regional, onde souberam do pacto São Francisco.', 'NULL', 'O vereador José de Araujo falou da visita na CAGECE regional, onde souberam do pacto São Francisco mas infelizmente a liberação de recurso não é totalmente confiável, lamentou que sua esposa contraiu chikungunya e  estranhou algumas postagens no facebook que diziam que o presidente e a mesa não eram a favor do movimento quando o proprío presidente esteve na manifestação. Sendo que no dia da manifestação todos os órgãos públicos fecharam, disse ver incoerência a movimentação ter sido em frente a Câmara quando em varios locais foram ao INSS, disse que o prefeito se exaltou não aceitando a manifestação dos professores o que é no mínimo incoerente, disse nunca ter visto um fato de um gestor se exaltar quando recebe vaias dos servidores que viraram as costas para o gestor de forma democrática, disse que na fala é uma coisa e na votação é totalmente diferente, disse que chegou o véto do gestor que toma uma hora, do planejamento dos professores e são essas atitudes que geram as reações vistas.', '/fotos/246/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/246/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(247, '2024-02-27 10:59:01', 'root', '2017-05-05', '', 'noticias', '#Administração', 'O vereador Francisco Juceza, parabenizou os colegas que estiveram em Fortaleza e os que foram a CAGECE.', 'NULL', 'O vereador Francisco Juceza, parabenizou os colegas que estiveram em Fortaleza e os que foram a CAGECE em Juazeiro, falou das dificuldades dos cedrenses pela falta da agência do Banco do Brasil, parabenizou o aniversariante Dr. Amarilo Sampaio, disse que acompanhou a manifestação do dia 28 do começo ao fim e ficou surpreso quando chegou aqui e encontrou a casa do povo fechada, disse que alguém da organização destratou todos os vereadores e provocou o prefeito que se indignou e fez o discurso, lembrou que o mesmo provocador disse que os vereadores estavam envergonhados por votarem em deputados que votaram contra o povo e mandando que os vereadores ligassem para os mesmos.', '/fotos/247/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/247/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(251, '2024-02-27 10:59:01', 'root', '2017-05-12', '', 'noticias', '#Administração', 'Imagens da ultima Sessão realizada na sede do Poder Legislativo, no dia 12/05/2017', 'NULL', '', '/fotos/251/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/251/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(253, '2024-02-27 10:59:02', 'root', '2017-05-19', '', 'noticias', '#Administração', 'Imagens da ultima Sessão realizada na Sede do poder Legislativo, no dia 19/05/2017.', 'NULL', '', '/fotos/253/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/253/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(254, '2024-02-27 10:59:02', 'root', '2017-05-19', '', 'noticias', '#Administração', 'A vereadora Silvana Leite, relatou a cerca da importância da mobilização do dia 18.', 'NULL', 'A vereadora Silvana Leite, relatou a cerca da importância da mobilização do dia 18, pelo combate a exploração sexual contra a Criança e Adolescente, parabenizou toda equipe com destaque para Isabela, coordenadora do CREAS sugerindo uma moção de congratulação para a mesma, destacou a solenidade de entrega de certificado do curso de bordado no Sítio Manoel Alexandre. Relatou sobre uma indicação de sua autoria para realização do Curso educando para o transito junto ao DETRAN.', '/fotos/254/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/254/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(255, '2024-02-27 10:59:02', 'root', '2017-05-19', '', 'noticias', '#Administração', 'A vereadora Ana Nilma Diniz, registrou o seminário de saúde da mulher.', 'NULL', 'A vereadora Ana Nilma Diniz, registrou o seminário de saúde da mulher ocorrido no auditório da saúde da Sec. De saúde, destacou que a mortalidade infantil e zero neste município. Lembrou a situação caótica em administrações passadas onde gestantes eram transferidas para o município de Icó. Relatou sobre a importância o SENAC e informou sobre a conclusão da licitação para a iluminação pública onde breve os serviços de reposição de lâmpadas serão retomadas.', '/fotos/255/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/255/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(256, '2024-02-27 10:59:02', 'root', '2017-05-19', '', 'noticias', '#Administração', 'O vereador Antonio Helio Bezerra, voltou a cobrar a reabertura do mercado.', 'NULL', 'O vereador Antonio Helio Bezerra, voltou a cobrar a reabertura do mercado ao gestor que busque orientação com o competente advogado Regis Albuquerque nas questões Juridicas, cobrou também solução com relação aos boxes da Praça Nilo Viana Diniz, falou que recebeu informações de Mal atendimento por parte de alguns funcionários da secretaria da saúde sugerindo a secretaria que realize capacitação com esses servidores públicos, informou que o senhor Zeno Falcão, funcionário da secretaria de cultura apresentou um projeto importante para a cultura local mas lamentavelmente a Secretária da pasta não aceitou o que acha lamentável, parabenizou o Dr. Samuel Viana pelo seu aniversário.', '/fotos/256/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/256/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(257, '2024-02-27 10:59:02', 'root', '2017-05-19', '', 'noticias', '#Administração', 'O vereador José de Araujo, inicio parabenizando seu primo Samuel Viana', 'NULL', 'O vereador José de Araujo, inicio parabenizando seu primo Samuel Viana e destacou suas qualidades dizendo tratar-se de um cidadão de coração enorme com compromisso com a população cedrense, lamentou mais uma vez a falta de compromisso do atual gestor com a população cedrense digo melhor com a classe dos secretários escolares, apelou por uma solução para o problemas da praça dos ferroviários lamentou os três anos esperando pela reabertura do mercado Central o que também apelou para que o gestor solucione o problema ressaltou que se existir na gestão coordenadores ou ocupantes de cargos comissionados ganhando sem trabalhar.', '/fotos/257/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/257/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(258, '2024-02-27 10:59:02', 'root', '2017-05-19', '', 'noticias', '#Administração', 'O vereador Amarilo Junior, agradeceu do secretario da infraestrutura.', 'NULL', 'O vereador Amarilo Junior, agradeceu do secretario da infraestrutura pela disponibilidade do mesmo em ter se deslocado até a comunidade de Angico para elaborar projeto de passagens molhadas, sugeriu o aterramento do buraco existente na praça dos ferroviários dizendo que concorda que há riscos de doenças, disse discordar que qualquer funcionário receba sem trabalhar, apelou pela reabertura do mercado público.', '/fotos/258/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/258/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(259, '2024-02-27 10:59:02', 'root', '2017-05-19', '', 'noticias', '#Administração', 'O vereador Francre Alves, informou que visitou um distrito do município de Saboeiro.', 'NULL', 'O vereador Francre Alves, informou que visitou um distrito do município de Saboeiro onde grande parte da população está acometida dessas doenças citadas pelo colega Juceza.  Citou também os buracos existentes na Praça dos Ferroviários que vem acumulando água parada, causando perigo para a população, indica para reforma no prédio da prefeitura municipal e solicitou mais vez a abertura dos banheiros do mercado José Fernandes  e finalizou parabenizando o medico Samuel Viana pelo seu aniversario.', '/fotos/259/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/259/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(260, '2024-02-27 10:59:02', 'root', '2017-05-19', '', 'noticias', '#Administração', 'O Vereador Matheus Araújo, reportou-se ao desabafo de um senhor do Sitio Lagoa dos Cavalos.', 'NULL', 'O Vereador Matheus Araújo, reportou-se ao desabafo de um senhor do Sitio Lagoa dos Cavalos através do Facebook afirmando está ao lado o mesmo que é conhecido como seu TIROL, lamentou o fato de atual gestor não ter doado horas e tratores para preparo das terras e relatou ainda que alguns técnicos fizeram medições em uma comunidade rural para projeta de Kits de irrigação e nunca mais voltaram ao local dizendo que irá buscar informação sobre esse caso, requereu verbalmente um serviço de aterramento por trás da estação de tratamento de água no Cariuzinho.', '/fotos/260/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/260/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(261, '2024-02-27 10:59:02', 'root', '2017-05-19', '', 'noticias', '#Administração', 'O Vereador Apolinário Gomes, solidarizou-se com os familiares do senhor Faé.', 'NULL', 'O Vereador Apolinário Gomes, solidarizou-se com os familiares do senhor Faé, do alto da Floresta pelo falecimento do mesmo, agradeceu aos, secretários de infraestrutura e meio ambiente pelo atendimento a requerimentos de sua autoria, informou que visitou a secretaria de agricultura com o colega Jose de Araújo, e foi bem recebido embora solicitou cópia do livro e ponto e não foram atendidos, disse que a subsecretaria tem que trabalhar e que fiscalizara nesse sentido.', '/fotos/261/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/261/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL);
INSERT INTO `noticias` (`id`, `data_cadastro`, `usuario`, `data_noticia`, `data_cadastro_formatada`, `tbl`, `categoria`, `titulo`, `resumo`, `descricao`, `imagem_nome`, `imagem_url`, `indice`, `id_entidade_orgaos`, `id_mandatos`, `id_admin`, `ativo`) VALUES
(262, '2024-02-27 10:59:02', 'root', '2017-05-19', '', 'noticias', '#Administração', 'O vereador Gilberto Barbosa, fez um relato da sua viagem a Brasília.', 'NULL', 'O vereador Gilberto Barbosa, fez um relato da sua viagem a Brasília, dizendo ter participado da marcha dos prefeitos um evento com mais de sete mil participantes momentos em que ocorreu uma importante reunião com a bancada cearense, e a APRECE que pautou pontos importantes na discursão, informou que junto ao gestor municipal foi reivindicado a ampliação do sistema de abastecimento de água local. E para finalizar destacou a equipe do Iguatu futebol clube, campeão da segunda divisão de futebol cearense, para quem também assegurou moção de congratulação.', '/fotos/262/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/262/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(264, '2024-02-27 10:59:02', 'root', '2017-05-26', '', 'noticias', '#Administração', 'O vereador Matheus Araújo parabenizou o doutor Samuel Viana pelo evento que arrecadou mais de tres mil quilos de alimentos.', 'NULL', 'O vereador Matheus Araújo parabenizou o doutor Samuel Viana pelo evento que arrecadou mais de três mil quilos de alimentos para beneficiar famílias carentes, enalteceu o Deputado Estadual Dr. Leonardo Pinheiro que esteve em Cedro e cumprirá os compromisso com essa terra inclusive com uma ambulância para o vale do São Miguel onde visitaram algumas comunidades do referido vale, solicitou o roço na estrada da localidade passagem molhada no Sítio Baixio da Areia e o piçarramento na estrada que liga a Várzea da Conceição ao sitio Cipó Grosso.', '/fotos/264/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/264/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(265, '2024-02-27 10:59:02', 'root', '2017-05-26', '', 'noticias', '#Administração', 'O vereador Francisco Juceza, disse que esteve em Fortaleza.', 'NULL', 'O vereador Francisco Juceza, disse que esteve em Fortaleza e verificou a cerca dos projetos dos açudes das comunidades de Pereiro e Varjota que na época da administração passada não houve a liberação de recursos por inadimplência do município. Também parabenizou o Médico Dr. Samuel Viana reformando o que foi dito pelos colegas, lembrou que a segurança é desejo de todos, mas as forças devem acontecer para buscar as melhorias junto ao governo do estado.', '/fotos/265/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/265/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(266, '2024-02-27 10:59:02', 'root', '2017-05-26', '', 'noticias', '#Administração', 'O vereador Gilberto Barbosa, parabenizou o Dr. Samuel Viana pelo aniversário.', 'NULL', 'O vereador Gilberto Barbosa, parabenizou o Dr. Samuel Viana pelo aniversário, falou que esteve no distrito de Santo Antônio no ultimo Sábado em evento do instituto Zuza Laurenio. Prestando contas da viagem a Brasília, disse que esteve em Fortaleza na Sec. De esporte junto ao representante do instituto Zuza e na quinta-feira esteve na reunião da elaboração do Plano Municipal diretor no bairro de Fátima como morador daquele bairro, disse que levou a questão do mercado publico para a assessoria jurídica e teve a informação que o processo esta avançando.', '/fotos/266/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/266/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(267, '2024-02-27 10:59:02', 'root', '2017-05-26', '', 'noticias', '#Administração', 'O vereador Antônio Hélio, falou do transito na avenida Eneas Viana e Pedro Lopes Vieira.', 'NULL', 'O vereador Antônio Hélio, falou do transito na avenida Eneas Viana e Pedro Lopes Vieira que está danificada pelo grande transito dizendo entender que por está dentro da cidade a competência é do município apelando para a gestão o conserto próximo ao bar do Jurandir no quebra molas e nas proximidades da churrascaria de Neto do Mirante, pediu agilidade no serviço que está sendo realizado na estrada da cidade no alto da floresta e apelou por uma limpeza com varredura em frente a churrascaria do senhor Neto, parabenizou o médico Samuel Viana pela festa que arrecadou alimentos que serão distribuídos com os carentes municipais.', '/fotos/267/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/267/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(268, '2024-02-27 10:59:02', 'root', '2017-05-26', '', 'noticias', '#Administração', 'O Vereador Marcelo Matos, destacou o trabalho do Padre Ademar Alves.', 'NULL', 'O Vereador Marcelo Matos, destacou o trabalho do Padre Ademar Alves em nossa Paroquia. Falou sobre a presença do deputado estadual Elmano de Freitas que em Cedro visitou o prédio do CSU em Várzea da Conceição apelando para o governo do Estado para uma ampla reforma naquele imóvel momento em que agradeceu ao Deputado acima citado, parabenizou os organizadores do PDDM plano diretor de desenvolvimento municipal ressaltando a importância dos encontros para ouvir a população.', '/fotos/268/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/268/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(269, '2024-02-27 10:59:02', 'root', '2017-05-26', '', 'noticias', '#Administração', 'O vereador José de Araújo relatou sobre os problemas na iluminação pública.', 'NULL', 'O vereador José de Araújo relatou sobre os problemas na iluminação pública, em seguido parabenizou o doutor Samuel Viana pela festa que arrecadou alimentos que será repassado aos carentes cedrenses e G6 ira oferecer o titulo de cidadão cedrense ao doutor Samuel Viana, falou dos cortes no orçamento do IFCE em nosso município onde haverá corte de 20% no quadro de terceirizados onde o professor Fernando vem lutando para amenizar os efeitos dessa crise, defendeu o projeto de Lei do colega Amarilo Junior, sobre a classe dos guardas municipais, disse que esteve na UVC em Fortaleza e recebeu as carteiras dos Vereados dos colegas.', '/fotos/269/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/269/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(270, '2024-02-27 10:59:02', 'root', '2017-05-26', '', 'noticias', '#Administração', 'O vereador Apolinário Gomes, parabenizou o doutor Samuel Viana pela linda festa realizada no ultimo sábado.', 'NULL', 'O vereador Apolinário Gomes, parabenizou o doutor Samuel Viana pela linda festa realizada no ultimo sábado, solicitou consertos de algumas bocas de lobo em algumas ruas, pediu patrolhamento para ruas na sede do distrito do Lajedo, disse que o aumento dos secretários escolares, ou seja a gratificação não saiu mas o prefeito já nomeou inúmeras pessoas nos meses de Abril e Maio e que sua voz esta sendo ouvida onde um servidor que não estava trabalhando e residente em Fortaleza já foi exonerado e a fiscalização continuará em todas as secretarias.', '/fotos/270/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/270/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(271, '2024-02-27 10:59:02', 'root', '2017-05-26', '', 'noticias', '#Administração', 'O Vereador Francre Alves, parabenizou o Doutor Samuel Viana pela festa realizada no seu aniversário no ultimo sábado.', 'NULL', 'O Vereador Francre Alves, parabenizou o Doutor Samuel Viana pela festa realizada no seu aniversário no ultimo sábado onde foi arrecadado três mil quilos de alimentos, falou sobre a preocupação com a onda de assaltos no município, falou do investimento do município com as câmeras de monitoramento e infelizmente funciona precariamente apelando ao gestor agilizar o conserto do sistema o mais breve possível citando a falta de iluminação pública em várias ruas, lamentou que os boxers da Praça da Matriz nunca foram entregues a população inclusive muitas obras estão paralisadas.', '/fotos/271/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/271/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(272, '2024-02-27 10:59:02', 'root', '2017-06-05', '', 'noticias', '#Administração', 'PREFEITOS CEARÁ 2017, GESTÃO EFICIENTE.', 'NULL', 'Os vereadores José de Araújo, Matheus Araújo, Marcelo Matos, Francre Alves e o Presidente da Câmara Municipal de Cedro Antônio Hélio Bezerra, participaram do evento “PREFEITOS CEARÁ 2017, GESTÃO EFICIENTE”, em Fortaleza onde teve a presença do Governador Estadual Camilo Santana e do Deputado Federal Chico Lopes.', '/fotos/272/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/272/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(273, '2024-02-27 10:59:02', 'root', '2016-06-02', '', 'noticias', '#Administração', 'Imagens da ultima sessão realizada na sede do Poder Legislativo de Cedro.', 'NULL', '', 'NULL', 'https://www.camaradecedro.ce.gov.brNULL', 0, NULL, 0, NULL, NULL),
(275, '2024-02-27 10:59:02', 'root', '2017-06-06', '', 'noticias', '#Administração', '\"PREFEITOS CEARÁ 2017, GESTÃO EFICIENTE\", segundo dia.', 'NULL', 'No segundo dia do \"PREFEITOS CEARÁ 2017, GESTÃO EFICIENTE\", o Secretario da Fazenda do estado do Ceará Mauro Filho, esteve presente.', '/fotos/275/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/275/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(276, '2024-02-27 10:59:02', 'root', '2017-06-09', '', 'noticias', '#Administração', 'Imagens da ultima sessão realizada na Sede do Poder Legislativo', 'NULL', '', '/fotos/276/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/276/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(277, '2024-02-27 10:59:02', 'root', '2017-06-09', '', 'noticias', '#Administração', 'O vereador Gilberto Barbosa e requereu o verbo aumente o áudio do seu pronunciamento.', 'NULL', 'O vereador Gilberto Barbosa e requereu o verbo aumente o áudio do seu pronunciamento, lamentou o ter sido informado que na sessão passada o seu pronunciamento foi interrompido às 11:54 Apelou para que seja cumprido o que é pago por esse poder até às 12:00 apelando para que a sessão inicia às 8:00 horas em ponto. Falou sobre a feira da Agricultura Familiar defendendo requerimento de autoria nesse sentido, falou sobre o abastecimento de água da Cachoeirinha dos Coelhos apelando para que o município viabilize a integração da rede para atender o sítio Esperança, prestou contas do seu trabalho como vereador onde esteve na rádio Mandacaru, na palestra sobre o meio ambiente no Distrito de Santo Antônio, pelo secretário Pitombeira dizendo ter ficado muito satisfeito com os alunos da Escola Antônio Laurentino de Oliveira, convidou a todos para os festejos do Padroeiro Santo Antônio, convidando para o leilão amanhã. Comprometeu-se em trazer os valores do recurso gasto no Município dentro do limite prudencial, relatou sobre o trabalho da secretaria do meio ambiente detalhando sobre a coleta de lixo apresentando os avanços no município com a coleta de lixo nos distritos inclusive com o trabalho importante de conscientização, disse que em 2016 a avaliação era 8,75 em 2017 permanece no mesmo nível onde os municípios cearenses o Cedro está na colocação 81, disse que o episódio Infeliz envolvendo o senhor o Udário Vieira, Cleriton Júnior e Diniz Junior diz ter sido uma infelicidade mas são pessoas de bem e esse assunto não deve ser tratada nesta casa, onde foi vítima de alguém pago para denegrir hoje paga o preço e nunca trouxe para câmera postura de pessoa que hoje diz uma coisa amanhã diz outra e sugere que a questão seja tratada na justiça, disse que essa gestão é honesto e respeita o erário, disse que esporte lhe comove falando de sua trajetória como desportista desde 12 anos de idade, disse reconhecer que o campeonato não foi feito mas sempre existiu isso neste município, disse concordar que aconteça as cobranças mas devemos ter coerência com relação à disponibilidade dos recursos, falou sobre a atuação do secretário adjunto do meio ambiente que desenvolve suas funções acompanhando algumas turmas de trabalho .', '/fotos/277/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/277/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(278, '2024-02-27 10:59:02', 'root', '2017-06-09', '', 'noticias', '#Administração', 'O vereador Francisco César disse não concordar com a postura do Senhor o Udário Vieira.', 'NULL', 'O vereador Francisco César disse não concordar com a postura do Senhor o Udário Vieira, mas acha que o caminho é a justiça sem relacionar o fato particular com a administração pública e o prefeito não concorda com o fato e não se pode apagar o que o senhor Udário Vieira, Fez pelo Cedro. Disse que em Várzea Alegre o secretário escolar ganha mais no entanto trabalha os dois expediente, disse que assunto como a saúde deve estar na Pauta e o Cedro passou vergonha com carros da polícia federal rodando o Cedro e não se pode esquecer o passado quando se traz prejuízo no presente, disso que os dois secretários não atacaram os vereadores e estavam fora do horário de trabalho temos um prefeito Sério que mudou o rumo do município que tinha três praças quebradas e ainda é uma adutora Que representa muito para o município.', '/fotos/278/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/278/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(279, '2024-02-27 10:59:02', 'root', '2017-06-09', '', 'noticias', '#Administração', 'A vereadora Ana Nilma, parabenizou o Padre Ademar pelo seu aniversário.', 'NULL', 'A vereadora Ana Nilma, parabenizou o Padre Ademar pelo seu aniversário destacando as qualidades do mesmo, defendeu indicações para retirada de água em algumas ruas no município, solicitou parceria da prefeitura com a Cagece para melhoria da chegada da água no Planalto das cadeiras no cedro I,  ressaltou que mais de oito bilhões em benefícios entraram nesse município nos últimos 4 anos pela praga da corrupção perdemos recursos mais de 512 mil nas comunidades da Zona Rural onde em 2010 o recurso foi perdido no DNOCS, pela inadimplência da época, Pede o reconhecimento pelo que está sendo feito lembrando altos recursos que beneficiaram no esporte lamentando o retorno de quatrocentos e oitenta mil que foi perdido para o estádio Montevidéu dizendo que era sim uma situação gritante e hoje o gestor zela o recurso do município. Disse que o estardalhaço que está sendo feito com os áudios e menosprezando a inteligência do povo quando todos têm livre arbítrio e suas vidas particulares, e essa casa merece respeito com assunto de interesse do povo em geral disse que os três cidadãos do áudio são pessoas de bem, disse ter sido vítima do vereador Antônio Hélio, na chácara do Senhor Mateus onde bêbado sem camisa gravou um vídeo falando da mesma e não trouxe para essa casa e acha que isso deve ser levado para as vias judiciais, pois os assuntos não somam.', '/fotos/279/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/279/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(280, '2024-02-27 10:59:02', 'root', '2017-06-09', '', 'noticias', '#Administração', 'A vereadora Silvana leite, parabenizou o padre Ademar, pelo aniversário e pelo brilhante trabalho de evangelização.', 'NULL', 'A vereadora Silvana leite, parabenizou o padre Ademar, pelo aniversário e pelo brilhante trabalho de evangelização nesta Paróquia. Disse que o Cartório Eleitoral local será transferido para Iguatu, pedindo a atenção de todos nesse sentido apelando por uma comissão para junto ao TRE, junto às autoridades para tentar reverter a situação defendeu o requerimento para pavimentação asfáltica ligando Cedro, ao distrito do Arrojado e a operação tampa buraco na CE-153, falou da mobilização para o dia mundial  da erradicação do trabalho infantil dia 12 de junho, disse que o secretário de esporte até uma semana  atrás era competente e bom e depois dos áudios onde os mesmos estavam foram do horário de trabalho, fez colocações embora não Concorde acha que o que foi dito pelo secretário de esporte não ofendeu nenhum Vereador e como o gosto do esporte e fez uso de bebida se excedeu e acha que o assunto não deve ser tratado nessa Tribuna, onde tem muitos assuntos relevantes para serem tratados e já foi conversando com o senhor gestor que não vê o fato como motivo de demissão embora o mesmo não tenha concordado com o episódio ressaltou achar que não viu ataque ao colega Marcelo Mato, e sim ao PT nacional.', '/fotos/280/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/280/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(281, '2024-02-27 10:59:02', 'root', '2017-06-09', '', 'noticias', '#Administração', 'O vereador Apolinário Gomes de Souza, reforçou o seu pedido de conserto de esgoto na Rua Zé Pajé.', 'NULL', 'O vereador Apolinário Gomes de Souza, reforçou o seu pedido de conserto de esgoto na Rua Zé Pajé em frente à Jurandir do bar e agradeceu a solidariedade do Povo, pelo ato de agressão a qual repudia, disse que houve Desrespeito com os parlamentares através do áudio que foi veiculado na internet, disse que faz política pensando na população, disse que o empresário Udário Vidal ganhava R$ 3000 sem trabalhar e achou ruim a denúncia nesse sentido, disse que o senhor o Udário, não mora em cedro e quando investiu foi em Tururú e foi atingido pelo mesmo junto ao povo cedrense, disse que o Udário Vieira quando foi vereador não residiu em Cedro e quando o mesmo foi secretário de esporte foi demitido por falar mal do gestor como é costume dele e dessa vez serão tomadas as medidas cabíveis disse que a vida particular não deve ser exposta em mesa de bar, disse que o cedrense e o cearense gosta do Lula solidarizou-se com colega Marcelo Matos, disse que não conhece o colega vagabundo, babaca, nem gigolô e o senhor o Udário foi infeliz,  disse ainda que entrou na política para servir ao povo.', '/fotos/281/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/281/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(282, '2024-02-27 10:59:02', 'root', '2017-06-09', '', 'noticias', '#Administração', 'O vereador Francre Alve, ressaltou a importância da divulgação dos trabalhos do Legislativo.', 'NULL', 'O vereador Francre Alve, ressaltou a importância da divulgação dos trabalhos do Legislativo onde o povo toma conhecimento do trabalho dos vereadores, disse que busca o melhor sem perseguir ninguém disse que não foi concedida a gratificação aos secretários escolares e técnicos agrícolas, lamentou as declarações nos áudios que afetaram diretamente a imagem do poder Legislativo onde a revolta  talvez seja por conta da denúncia do seu cargo sem prestar o serviço e a função de fiscalizar sera continuada, lembrou que houve promessa de trazer a Marisol e não trouxe enganando o povo e agora é aguardar se chega essa empresa novamente foi prometida, disse que estiveram com o secretario Mauro Filho para tratar do estádio Montevideo e aguardam a reformulação do projeto e atualização do orçamento que não foi fornecido.', '/fotos/282/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/282/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(283, '2024-02-27 10:59:02', 'root', '2017-06-09', '', 'noticias', '#Administração', 'O Vereador José de Araújo, saudou o seu tio João Viana que se encontra no Sítio Várzea Feia.', 'NULL', 'O Vereador José de Araújo, saudou o seu tio João Viana que se encontra no Sítio Várzea Feia, lamentou o fato do município ter ficado fora da lista dos inabilitados sem a devida atenção da Secretaria de cultura, para o evento o que acontecia anteriormente, disse que o Cedro está em quatro a seis por cento do limite prudencial com uma larga margem de oito por cento, mas com uma desculpa esfarrapada pelo executivo indagando para onde está esse recurso, disse que a população está sendo enganada e o trabalho sera feito nesta casa onde a voz do povo é ouvida e se não tivessem tomado providencias o morador de Turuna ainda estava recebendo sem trabalhar, disse que a prefeitura virou um cabide de emprego de pessoas incapacitadas dando emprego eleitoral e recomendou o senhor Zeno Falcão a ser candidato a vereador e sugeriu a convocação da secretário de cultura para explicações nessa, disse que o que foi feito não é papel de Secretario, ou o prefeito demite ou torna os secretários espelhos de sua administração, apresentou repudio e total revolta, disse que na época do campeonato máster deu como então secretário de esporte total apoio para o evento.', '/fotos/283/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/283/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(284, '2024-02-27 10:59:02', 'root', '2017-06-09', '', 'noticias', '#Administração', 'O vereador Amarilo Junior, que atiram no que ver mas acera no que não ver, agradeceu pela solidariedade de muitos pelo fato em tela,', 'NULL', 'O vereador Amarilo Junior, que atiram no que ver mas acera no que não ver, agradeceu pela solidariedade de muitos pelo fato em tela, justificou as moções de repúdio aos autores dos áudios, disse que a imagem do Povo Cedrense foi atingida com a atingida com a atitude dos autores dos áudios, quem se diz líder político e gosta de Cedro não faz esse tipo de coisa e ajuda deve ser indistinta e sua resposta e em nome da população que em grande parte se encontra chocada, solidarizou-se com o colega Marcelo Matos que foi atingido junto a Darcílio e Esmeraldina e o PT obteve mais de onze mil eleitores que votaram na Dilma de desonestos disse que precisa repudiar e o Secretário de esporte foi infeliz quando deixou subentendido que a secretaria de esporte local está falida, onde apelas aos Vereadores por uma caixinha o que apela uma analisa dos desportistas local, prestou homenagem ao seu pai Amarilo Sampaio, e ressaltou que para ser bom não é preciso dizer que os outros são ruins.', '/fotos/284/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/284/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(285, '2024-02-27 10:59:02', 'root', '2017-06-09', '', 'noticias', '#Administração', 'O vereador Matheus Araújo prestou contas de sua viagem a Fortaleza, falando do seminário dos prefeitos.', 'NULL', 'O vereador Matheus Araújo prestou contas de sua viagem a Fortaleza, falando do seminário dos prefeitos na capital, falou do encontro com o Deputado Moises Braga em Iguatu, parabenizou o Padre Ademar pelo aniversário. Lamentou os áudios e as atitudes dos senhores Udário Vieira, Cleriton Junior e Diniz Junior, disse esta de coração amargurado e não aceita ser chamado de vagabundo e babaca e o grupo de oposição está desempenhando seu papel e que a fiscalização continuará com intensidade. Disse que patrocínio não e da competência dos vereadores pois existe a Secretaria com verbas especificas para isso ou façam projetos e busquem recursos e o Secretario Cleriton e o Senhor Udário Vieira tem feito pouco pelo esporte, solidarizou-se com o senhor João Ernesto da Várzea, perguntou se a fábrica norueguesa se  será igual à da Marisol, disse se falta de inteligência do senhor Udario Vieira, apelou ao mesmo que justifique os áudios com falta de respeito na mesa do bar talvez pela exoneração do cargo no poder Público deste município sem nunca ter desempenhado devidamente suas funções.', '/fotos/285/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/285/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
(286, '2024-02-27 10:59:02', 'root', '2017-06-16', '', 'noticias', '#Administração', 'Fotos da sessão ordinária realizada no dia 16/06/2017.', 'NULL', 'Fotos da sessão ordinária realizada no dia 16/06/2017.', '/fotos/286/Img0_600x400.jpg', 'https://www.camaradecedro.ce.gov.br/fotos/286/Img0_600x400.jpg', 0, NULL, 0, NULL, NULL),
